    const GLchar *source = [content UTF8String];

    //创建对应类型的shader
    *shader = glCreateShader(type);

    //将着色器附着到 着色器对象上
    glShaderSource(*shader, 1, &source, NULL);

    //编译
    glCompileShader(*shader);
}
