@property (nonatomic, strong) EAGLContext *context;//上下文

@property (nonatomic, assign) GLuint renderBuffer;//渲染缓冲区ID
@property (nonatomic, assign) GLuint frameBuffer;//帧缓冲区ID

@property (nonatomic, assign) GLuint program;//程序ID

@end
